    public int largestMagicSquare(int[][] grid) {

        int ans = 0;

        int w = grid.length,h = grid[0].length;
        long[][] prefix_w = new long[w][h+1];
        long[][] prefix_h = new long[h][w+1];

        for (int i = 0; i < w; i++) {
            for (int j = 0; j < h; j++) {
                prefix_w[i][j+1] = prefix_w[i][j]+ grid[i][j];
                prefix_h[j][i+1] = prefix_h[j][i] + grid[i][j];
            }
        }


      for (int r = 0; r < w; r++) {
        for (int c = 0; c < h; c++) {
            for (int r1 = 0; r1 <= r; ++r1) {
                loop2:for (int c1 = 0; c1 <= c; ++c1) {
                    if (c - c1 == r - r1) {
                        int span = r - r1 + 1;
                        long width = prefix_w[r1][c+1] - prefix_w[r1][c1];
                        long cross1 = grid[r1][c1],cross2=0;

                        for (int i = r1+1; i <= r; ++i){
                            cross1 += grid[i][c1 + (i-r1)];
                            if(prefix_w[i][c+1] - prefix_w[i][c1]!=width)continue loop2;
                        }
                        for (int i = c1; i <= c; ++i){
                            cross2 += grid[r1 + (i-c1)][i];
                            if(prefix_h[i][r+1] - prefix_h[i][r1]!=width )continue loop2;
                        }

                        if(cross1==width && cross2==width && span>ans){
                            ans = span;
                            System.out.println(span+":"+width+"-----"+r1+":"+c1+";"+r+":"+c);
                            break ;
                        }



                    }
                }
            }
        }
    }

      return ans;
}
